"#styled-checkbox").click(function () {     $('input:checkbox').not(this).prop('checked', this.checked); });function filterFunction(){ 		    var input, filter, ul, li, a, i;		    input = document.getElementById("myInput");			//alert(input);		    filter = input.value.toUpperCase();		    div = document.getElementById("myDropdown");		    a = div.getElementsByTagName("a");		    for (i = 0; i < a.length; i++) {		        if (a[i].innerHTML.toUpperCase().indexOf(filter) > -1) {		            a[i].style.display = "";		        } else {		            a[i].style.display = "none";		        }		    }		}</script>